<script setup>
import { Link, router } from "@inertiajs/vue3";
import { ref, onMounted, onBeforeUnmount, defineEmits } from "vue";
import { languages } from "@/Helper/MultiLanguage.js";
import SubHeader from "@/Components/Header/SubHeader.vue";

const props = defineProps({
    userInfo: Object,
    selectLanguage: Boolean,
});
const emits = defineEmits();
const isSelectLanguage = ref(false);
const selectedLanguage = ref(localStorage.getItem("selectedLanguage") || "jp");
const selectLanguage = ref(props.selectLanguage);

const handleSelectLanguageStep = () => {
    isSelectLanguage.value = true;
    emits('selectLanguage', true);
};
const handleBackToMenu = () => {
    isSelectLanguage.value = false;
    emits('selectLanguage', false);
};
const handleSelectLanguage = (lang) => {
    selectedLanguage.value = lang;
    localStorage.setItem("selectedLanguage", lang);
    router.get(route("setlang", { locale: lang }));
    isSelectLanguage.value = false;
    emits('selectLanguage', false);
};

const isMobile = ref(window.innerWidth < 768);
const windowWidth = ref(window.innerWidth);

onMounted(() => {
    selectedLanguage.value = localStorage.getItem("selectedLanguage") || "jp";

    const handleResize = () => {
        windowWidth.value = window.innerWidth;
        isMobile.value = window.innerWidth < 768;
    };

    window.addEventListener("resize", handleResize);

    onBeforeUnmount(() => {
        window.removeEventListener("resize", handleResize);
    });
});
</script>
